const create_table_arboles = `CREATE TABLE IF NOT EXISTS arboles (
    _id	INTEGER NOT NULL,
    especie	TEXT,
    autor	TEXT,
    nombre1	TEXT,
    nombre2	TEXT,
    nombre3	TEXT,
    nombre4	TEXT,
    nombre5	TEXT,
    nombre6	TEXT,
    nombre7	TEXT,
    formadevida	TEXT,
    presenciaespinas	TEXT,
    disposicionhoja	TEXT,
    tipohoja	TEXT,
    formahoja	TEXT,
    bordehoja	TEXT,
    colorflor	TEXT,
    tipofruto	TEXT,
    familia	TEXT,
    caracteristicas	TEXT,
    habitatcaracteristicas	TEXT,
    estatus	TEXT,
    observaciones	TEXT,
    PRIMARY KEY(_id)
  );`;

